import 'package:flutter/foundation.dart';
import 'package:freezed_annotation/freezed_annotation.dart';
import '../../../../core/domain/utils_and_services/helpers.dart';

part 'todo_model_immutable.freezed.dart';

/// üìù Immutable model for Todo item (Freezed-based)
@freezed
class ImmutableTodoModel with _$ImmutableTodoModel {
  /// Main constructor
  const factory ImmutableTodoModel({
    required String id,
    required String description,
    @Default(false) bool completed,
  }) = _ImmutableTodoModel;

  /// Factory for creating a new Todo with autogenerated id
  factory ImmutableTodoModel.add({required String description}) {
    return ImmutableTodoModel(id: uuid.v4(), description: description);
  }
}
